"use strict";new Vue({el:"#app",data:{filter_market:[{name:"不限",market:"all",isNow:!0},{name:"債券型",market:"bond",isNow:!1},{name:"成熟股",market:"mature",isNow:!1},{name:"新興股",market:"rising",isNow:!1},{name:"產業",market:"industry",isNow:!1}],filter_weather:[{name:"不限",weather:"all",isNow:!0},{name:"樂觀",weather:"optimism",isNow:!1},{name:"中立",weather:"neutral",isNow:!1},{name:"悲觀",weather:"pessimistic",isNow:!1}],sort:{menuIsOpen:!1,list:[{text:"3個月績效排序",sort_month:"3",isNow:!0,isHover:!1},{text:"6個月績效排序",sort_month:"6",isNow:!1,isHover:!1},{text:"1年績效排序",sort_month:"12",isNow:!1,isHover:!1}]}},methods:{market_switchClass:function(s){var t=this.filter_market,i=t.length;if("0"==s){for(var e=1;e<i;e++)t[e].isNow=!1;t[s].isNow=!0}else t[0].isNow=!1,t[s].isNow=!t[s].isNow},weather_switchClass:function(s){var t=this.filter_weather,i=t.length;if("0"==s){for(var e=1;e<i;e++)t[e].isNow=!1;t[s].isNow=!0}else t[0].isNow=!1,t[s].isNow=!t[s].isNow},switch_sortMenu:function(){this.sort.menuIsOpen=!this.sort.menuIsOpen},close_sortMenu:function(){this.sort.menuIsOpen=!1},li_isHover:function(s){this.sort.list[s].isHover=!0},li_isNotHover:function(s){this.sort.list[s].isHover=!1},li_select:function(s){for(var t=this.sort.list.length,i=0;i<t;i++)this.sort.list[i].isNow=!1;this.sort.list[s].isNow=!0}},computed:{}});